			u.TELEPHONE = #{telephone}


			AND u.LOGINNAME = #{loginName}

mapper.insert:
INSERT INTO
( USERNAME, TELEPHONE, PASSWORD, EMAIL, BIRTHDAY, IMGURL, SEX, REALNAME, QQ, USERCOMPANY, NOTE, CUSTOMERSOURCE, WECHAT, KINDLY_SEND, FOLLOW_TIME, UPDATE_TIME, REFERRERID, OFFICIALSITE, POSITION, CUSTOMERTYPE, PURCHASEFREQUENCY, PURCHASEPRICE, CUSTOMERSIZE, ENDORSE
,CLIENTLEVEL
,PREFERENCE
) VALUES( #{userName}, #{telephone}, #{password}, #{email}, DATE_FORMAT(#{birthday},'%Y-%m-%d'), #{imgUrl}, ${sex}, #{realName}, #{qq}, #{userCompany}, #{note}, #{customerSource}, #{weChat}, #{kindlySend}, #{followTime}, CURRENT_TIMESTAMP, #{referrerId}, #{officialSite}, #{position}, #{customerType}, #{purchaseFrequency}, #{purchasePrice}, #{customerSize}, #{endorse}
,${clientLevel}
,${preference}
)

mapper.update:
UPDATE
USERNAME = #{userName}, SEX = ${sex}, REALNAME = #{realName}, email = #{email}, qq = #{qq}, WECHAT = #{weChat}, UPDATE_TIME = CURRENT_TIMESTAMP, USERCOMPANY = #{userCompany}, CUSTOMERSOURCE = ${customerSource}, IMGURL = #{imgUrl}
id = #{id}

mapper.select:
SELECT u.ID AS id, u.USERNAME AS userName, u.PASSWORD AS password, u.TELEPHONE AS telephone, u.EMAIL AS email, u.IMGURL AS imgUrl, u.SEX AS sex, u.QQ AS qq, u.REALNAME as realName, u.USERCOMPANY AS userCompany, u.CLIENTLEVEL AS clientLevel, DATE_FORMAT(u.BIRTHDAY,'%Y-%m-%d') AS birthday, DATE_FORMAT(u.CREATEDATE,'%Y-%m-%d %T') AS createDate, NOTE AS note, u.CUSTOMERSOURCE AS customerSource, u.WECHAT AS weChat, u.KINDLY_SEND AS kindlySend, u.PREFERENCE AS preference, DATE_FORMAT(u.UPDATE_TIME,'%Y-%m-%d %T') AS updateTime, DATE_FORMAT(u.FOLLOW_TIME,'%Y-%m-%d') AS followTime, u.REFERRERID AS referrerId, u.OFFICIALSITE AS officialSite, u.POSITION AS position, u.CUSTOMERTYPE AS customerType, u.PURCHASEFREQUENCY AS purchaseFrequency, u.PURCHASEPRICE AS purchasePrice, u.CUSTOMERSIZE AS customerSize, u.ENDORSE AS endorse FROM u
u.USERNAME LIKE CONCAT(CONCAT('%',#{userName}),'%')
AND u.REALNAME LIKE CONCAT(CONCAT('%',#{realName}),'%')
AND u.USERCOMPANY LIKE CONCAT(CONCAT('%',#{userCompany}),'%')
AND u.TELEPHONE LIKE CONCAT(CONCAT('%',#{telephone}),'%')
AND u.OFFICIALSITE LIKE CONCAT(CONCAT('%',#{officialSite}),'%')
AND u.CUSTOMERTYPE = #{customerType}
AND u.PURCHASEFREQUENCY = #{purchaseFrequency}
AND u.PURCHASEPRICE = #{purchasePrice}
AND u.CUSTOMERSIZE = #{customerSize}
AND u.ENDORSE = #{endorse}
AND u.CUSTOMERTYPE = #{customerType}
AND u.clientLevel IS NULL
AND u.clientLevel = ${clientLevel}
ORDER BY
${sort} ${order},
CREATEDATE,ID DESC LIMIT ${begin} , ${limit}

SELECT COUNT(1) 
FROM 
 u


	u.USERNAME LIKE CONCAT(CONCAT('%',#{userName}),'%')



			AND u.REALNAME LIKE CONCAT(CONCAT('%',#{realName}),'%')




			AND u.USERCOMPANY LIKE CONCAT(CONCAT('%',#{userCompany}),'%')



		AND u.TELEPHONE LIKE CONCAT(CONCAT('%',#{telephone}),'%')



			AND u.OFFICIALSITE LIKE CONCAT(CONCAT('%',#{officialSite}),'%')




			AND u.CUSTOMERTYPE = #{customerType}




			AND u.PURCHASEFREQUENCY = #{purchaseFrequency}




			AND u.PURCHASEPRICE = #{purchasePrice}




			AND u.CUSTOMERSIZE = #{customerSize}




			AND u.ENDORSE = #{endorse}




			AND u.CUSTOMERTYPE = #{customerType}




			AND u.clientLevel IS NULL



			AND u.clientLevel = ${clientLevel}

SELECT u.ID AS id, u.USERNAME AS userName, u.TELEPHONE AS telephone, u.REALNAME as realName, u.USERCOMPANY AS userCompany FROM u
u.ID IN
#{item}
