mapper.select:
SELECT COUNT(1)
	FROM 
	 u


			u.TELEPHONE = #{telephone}


			AND u.LOGINNAME = #{loginName}

mapper.insert:
INSERT INTO
( USERNAME, TELEPHONE, PASSWORD, EMAIL, BIRTHDAY, IMGURL, SEX, REALNAME, QQ, USERCOMPANY, NOTE, CUSTOMERSOURCE, WECHAT, KINDLY_SEND, FOLLOW_TIME, UPDATE_TIME
,CLIENTLEVEL
,PREFERENCE
) VALUES( #{userName}, #{telephone}, #{password}, #{email}, DATE_FORMAT(#{birthday},'%Y-%m-%d'), #{imgUrl}, ${sex}, #{realName}, #{qq}, #{userCompany}, #{note}, #{customerSource}, #{weChat}, #{kindlySend}, #{followTime}, CURRENT_TIMESTAMP
,${clientLevel}
,${preference}
)

mapper.update:
UPDATE
USERNAME = #{userName}, SEX = ${sex}, REALNAME = #{realName}, email = #{email}, qq = #{qq}, WECHAT = #{weChat}, UPDATE_TIME = CURRENT_TIMESTAMP, USERCOMPANY = #{userCompany}, CUSTOMERSOURCE = ${customerSource}, IMGURL = #{imgUrl}
id = #{id}

mapper.select:
SELECT u.ID AS id, u.USERNAME AS userName, u.PASSWORD AS password, u.TELEPHONE AS telephone, u.EMAIL AS email, u.IMGURL AS imgUrl, u.SEX AS sex, u.QQ AS qq, u.REALNAME as realName, u.USERCOMPANY AS userCompany, u.CLIENTLEVEL AS clientLevel, DATE_FORMAT(u.BIRTHDAY,'%Y-%m-%d') AS birthday, DATE_FORMAT(u.CREATEDATE,'%Y-%m-%d %T') AS createDate, NOTE AS note, u.CUSTOMERSOURCE AS customerSource, u.WECHAT AS weChat, u.KINDLY_SEND AS kindlySend, u.PREFERENCE AS preference, DATE_FORMAT(u.UPDATE_TIME,'%Y-%m-%d %T') AS updateTime, DATE_FORMAT(u.FOLLOW_TIME,'%Y-%m-%d') AS followTime FROM u
u.USERNAME LIKE CONCAT(CONCAT('%',#{userName}),'%')
AND u.TELEPHONE LIKE CONCAT(CONCAT('%',#{telephone}),'%')
AND DATE_FORMAT(u.FOLLOW_TIME,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(u.FOLLOW_TIME,'%Y-%m-%d') <= #{endTime}
AND u.clientLevel IS NULL
AND u.clientLevel = ${clientLevel}
ORDER BY
${sort} ${order},
CREATEDATE,ID DESC LIMIT ${begin} , ${limit}
